                type="submit"
                className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700"
              >
                Confirm Booking
              </button>
            </div>
          </form>
        </div>
      </div>
    );
  };

  // Updated Appointments Component
  const Appointments = () => {
    const [slots, setSlots] = useState([]);
    const [appointments, setAppointments] = useState([]);
    const [selectedSlot, setSelectedSlot] = useState(null);
    const { token, logout } = React.useContext(AuthContext);
    const navigate = useNavigate();

    useEffect(() => {
      fetchData();
    }, []);

    const fetchData = async () => {
      try {
        const [slotsRes, appointmentsRes] = await Promise.all([
          fetch('http://localhost:3001/api/slots', {
            headers: { Authorization: `Bearer ${token}` },
          }),
          fetch('http://localhost:3001/api/appointments', {
            headers: { Authorization: `Bearer ${token}` },
          }),
        ]);

        if (slotsRes.ok && appointmentsRes.ok) {
          const [slotsData, appointmentsData] = await Promise.all([
            slotsRes.json(),
            appointmentsRes.json(),
          ]);
          setSlots(slotsData);
          setAppointments(appointmentsData);
        }
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    const bookAppointment = async (slotId, bookingDetails) => {
      try {
        const response = await fetch('http://localhost:3001/api/appointments', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify({
            slot_id: slotId,
            ...bookingDetails
          }),
        });

        if (response.ok) {
          setSelectedSlot(null);
          fetchData();
        }
      } catch (error) {
        console.error('Error booking appointment:', error);
      }
    };

    const cancelAppointment = async (appointmentId) => {
      try {
        const response = await fetch(`http://localhost:3001/api/appointments/${appointmentId}`, {
          method: 'DELETE',
          headers: { Authorization: `Bearer ${token}` },
        });

        if (response.ok) {
          fetchData();
        }
      } catch (error) {
        console.error('Error cancelling appointment:', error);
      }
    };

    return (
      <div className="min-h-screen bg-gray-50 p-8">
        <div className="max-w-4xl mx-auto space-y-8">
          <div className="flex justify-between items-center">
            <h1 className="text-3xl font-bold">Appointment Booking</h1>
            <button
              onClick={logout}
              className="px-4 py-2 bg-red-600 text-white rounded-md hover:bg-red-700"
            >
              Logout
            </button>
          </div>

          <div className="bg-white rounded-lg shadow p-6">
            <h2 className="text-xl font-semibold mb-4">Available Slots</h2>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
              {slots.map((slot) => (
                <div
                  key={slot.id}
                  className="border rounded-md p-4 hover:shadow-md transition-shadow"
                >
                  <p className="font-medium">
                    {new Date(slot.start_time).toLocaleString()}
                  </p>
                  <button
                    onClick={() => setSelectedSlot(slot)}
                    className="mt-2 w-full py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700"
                  >
                    Book
                  </button>
                </div>
              ))}
            </div>
          </div>

          <div className="bg-white rounded-lg shadow p-6">
            <h2 className="text-xl font-semibold mb-4">Your Appointments</h2>
            <div className="space-y-4">
              {appointments.map((appointment) => (
                <div
                  key={appointment.id}
                  className="border rounded-md p-4 flex justify-between items-center"
                >
                  <div>
                    <p className="font-medium">
                      {new Date(appointment.start_time).toLocaleString()}
                    </p>
                    <p className="text-gray-600">
                      Duration: {Math.round(
                        (new Date(appointment.end_time) - new Date(appointment.start_time)) / 
                        (1000 * 60)
                      )} minutes
                    </p>
                    {appointment.notes && (
                      <p className="text-gray-600 mt-1">Notes: {appointment.notes}</p>
                    )}
                  </div>
                  <button
                    onClick={() => cancelAppointment(appointment.id)}
                    className="px-4 py-2 bg-red-600 text-white rounded-md hover:bg-red-700"
                  >
                    Cancel
                  </button>
                </div>
              ))}
            </div>
          </div>
        </div>

        {selectedSlot && (
          <BookingModal
            slot={selectedSlot}
            onClose={() => setSelectedSlot(null)}
            onBook={bookAppointment}
          />
        )}
      </div>
    );
  };

// App Component
const App = () => {
 return (
   <AuthProvider>
     <Router>
       <Routes>
         <Route path="/login" element={<Login />} />
         <Route
           path="/appointments"
           element={
             <ProtectedRoute>
               <Appointments />
             </ProtectedRoute>
           }
         />
         <Route path="*" element={<Navigate to="/appointments" replace />} />
       </Routes>
     </Router>
   </AuthProvider>
 );
};

// Protected Route Component
const ProtectedRoute = ({ children }) => {
 const { token } = React.useContext(AuthContext);
 const navigate = useNavigate();

 useEffect(() => {
   if (!token) {
     navigate('/login');
   }
 }, [token, navigate]);

 return token ? children : null;
};

export default App;
