// Calendar.js
import React from 'react';
import { useState, useEffect } from 'react';
import { format, startOfMonth, endOfMonth, eachDayOfInterval, isEqual, isSameDay } from 'date-fns';

const Calendar = ({ onDateSelect }) => {
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [availableDates, setAvailableDates] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchAvailableDates();
  }, []);

  const fetchAvailableDates = async () => {
    try {
      setLoading(true);
      const response = await fetch('http://localhost:3000/api/available-dates');
      
      if (!response.ok) {
        const errorData = await response.json(); // Add this line to log the error details
        console.error('Error details:', errorData);
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      
      const data = await response.json();
      setAvailableDates(data.map(slot => ({
        date: new Date(slot.date),
        time: slot.time,
        id: slot.id
      })));
    } catch (error) {
      console.error('Error fetching available dates:', error);
      setError('Failed to load available dates');
    } finally {
      setLoading(false);
    }
  };
  const getDaysInMonth = () => {
    const start = startOfMonth(selectedDate);
    const end = endOfMonth(selectedDate);
    return eachDayOfInterval({ start, end });
  };

  const isDateAvailable = (date) => {
    return availableDates.some(slot => isSameDay(slot.date, date));
  };

  const handleDateClick = (date) => {
    if (isDateAvailable(date)) {
      setSelectedDate(date);
      // Find all available time slots for the selected date
      const timeSlots = availableDates.filter(slot => 
        isSameDay(slot.date, date)
      );
      onDateSelect(date, timeSlots);
    }
  };

  if (loading) {
    return <div className="text-center p-4">Loading calendar...</div>;
  }

  if (error) {
    return <div className="text-center p-4 text-red-500">{error}</div>;
  }

  return (
    <div className="max-w-4xl mx-auto mt-8">
      <div className="grid grid-cols-7 gap-2">
        {['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'].map((day) => (
          <div key={day} className="text-center font-bold p-2">
            {day}
          </div>
        ))}
        {getDaysInMonth().map((date) => {
          const isAvailable = isDateAvailable(date);
          const isSelected = isSameDay(date, selectedDate);
          
          return (
            <div
              key={date.toString()}
              onClick={() => handleDateClick(date)}
              className={`p-4 border rounded ${
                isAvailable ? 'cursor-pointer' : 'cursor-not-allowed opacity-50'
              } ${
                isSelected
                  ? 'bg-blue-500 text-white'
                  : isAvailable 
                    ? 'hover:bg-gray-100'
                    : ''
              }`}
            >
              {format(date, 'd')}
            </div>
          );
        })}
      </div>
    </div>
  );
};

// BookingForm.js
const BookingForm = ({ selectedDate, availableSlots, onBookingComplete }) => {
  const [bookingData, setBookingData] = useState({
    timeSlot: '',
    notes: '',
    name: '',
    contact: ''
  });
  const [errorMessage, setErrorMessage] = useState('');
  const [successMessage, setSuccessMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setErrorMessage('');
    setSuccessMessage('');

    try {
      const response = await fetch('http://localhost:3000/api/appointments', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify({
          ...bookingData,
          date: format(selectedDate, 'yyyy-MM-dd')
        }),
      });

      const data = await response.json();

      if (response.ok) {
        setSuccessMessage('Appointment booked successfully!');
        setTimeout(() => {
          onBookingComplete();
        }, 2000);
      } else {
        setErrorMessage(data.error || 'Booking failed. Please try again.');
      }
    } catch (error) {
      console.error('Booking failed:', error);
      setErrorMessage('An error occurred. Please try again.');
    }
  };

  return (
    <div className="max-w-md mx-auto mt-8 p-6 bg-white rounded-lg shadow-md">
      <h3 className="text-xl font-bold mb-4">
        Book Appointment for {format(selectedDate, 'MMMM d, yyyy')}
      </h3>
      {errorMessage && (
        <div className="mb-4 p-2 bg-red-100 text-red-600 border border-red-200 rounded">
          {errorMessage}
        </div>
      )}
      {successMessage && (
        <div className="mb-4 p-2 bg-green-100 text-green-600 border border-green-200 rounded">
          {successMessage}
        </div>
      )}
      <form onSubmit={handleSubmit}>
        <div className="mb-4">
          <label className="block text-gray-700 mb-2">Name</label>
          <input
            type="text"
            className="w-full p-2 border rounded"
            value={bookingData.name}
            onChange={(e) => setBookingData({ ...bookingData, name: e.target.value })}
            required
          />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700 mb-2">Contact Number</label>
          <input
            type="tel"
            className="w-full p-2 border rounded"
            value={bookingData.contact}
            onChange={(e) => setBookingData({ ...bookingData, contact: e.target.value })}
            required
          />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700 mb-2">Time Slot</label>
          <select
            className="w-full p-2 border rounded"
            value={bookingData.timeSlot}
            onChange={(e) => setBookingData({ ...bookingData, timeSlot: e.target.value })}
            required
          >
            <option value="">Select a time</option>
            {availableSlots.map((slot) => (
              <option key={slot.id} value={slot.id}>
                {slot.time}
              </option>
            ))}
          </select>
        </div>
        <div className="mb-4">
          <label className="block text-gray-700 mb-2">Notes (Optional)</label>
          <textarea
            className="w-full p-2 border rounded"
            value={bookingData.notes}
            onChange={(e) => setBookingData({...bookingData, notes: e.target.value})}
            rows="3"
          />
        </div>
        <button 
          type="submit"
          className="w-full bg-blue-500 text-white p-2 rounded hover:bg-blue-600"
        >
          Book Appointment
        </button>
      </form>
    </div>
  );
};

export { Calendar, BookingForm };
