import React, { useState, useEffect } from 'react';
import { format, startOfMonth, endOfMonth, eachDayOfInterval } from 'date-fns';

// Calendar Component
const Calendar = ({ onDateSelect }) => {
  const [selectedDate, setSelectedDate] = useState(new Date());

  const getDaysInMonth = () => {
    return eachDayOfInterval({ start: startOfMonth(selectedDate), end: endOfMonth(selectedDate) });
  };

  return (
    <div className="max-w-4xl mx-auto mt-8">
      <div className="grid grid-cols-7 gap-2">
        {['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'].map(day => (
          <div key={day} className="text-center font-bold p-2">{day}</div>
        ))}
        {getDaysInMonth().map(date => (
          <div
            key={date.toString()}
            onClick={() => {
              console.log("Clicked on date:", format(date, 'yyyy-MM-dd'));
              onDateSelect(date);
            }}
            className="p-4 border rounded cursor-pointer text-center hover:bg-gray-100"
          >
            {format(date, 'd')}
          </div>
        ))}
      </div>
    </div>
  );
};

// Booking Form Component
const BookingForm = ({ selectedDate, availableSlots, onBookingComplete }) => {
  const [bookingData, setBookingData] = useState({ timeSlot: '', notes: '' });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('/api/appointments', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json', 'Authorization': `Bearer ${localStorage.getItem('token')}` },
        body: JSON.stringify({ ...bookingData, date: format(selectedDate, 'yyyy-MM-dd') }),
      });
      if (response.ok) onBookingComplete();
    } catch (error) {
      console.error('Booking failed:', error);
    }
  };

  return availableSlots.length > 0 ? (
    <form onSubmit={handleSubmit}>
      <select value={bookingData.timeSlot} onChange={(e) => setBookingData({...bookingData, timeSlot: e.target.value})}>
        <option value="">Select a time</option>
        {availableSlots.map(slot => <option key={slot.id} value={slot.time}>{slot.time}</option>)}
      </select>
      <button type="submit">Book Appointment</button>
    </form>
  ) : <p>No available slots.</p>;
};

export { Calendar, BookingForm };
