import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
    const [slots, setSlots] = useState([]);
    const [bookings, setBookings] = useState([]);
    const [formData, setFormData] = useState({ user_name: '', user_email: '', date: '', time_slot: '' });

    useEffect(() => {
        fetchSlots();
    }, []);

    const fetchSlots = async () => {
        const response = await axios.get('http://localhost:5000/slots');
        setSlots(response.data);
    };

    const fetchBookings = async (email) => {
        const response = await axios.get(`http://localhost:5000/appointments?email=${email}`);
        setBookings(response.data);
    };

    const handleBookAppointment = async (e) => {
        e.preventDefault();
        await axios.post('http://localhost:5000/appointments', formData);
        fetchBookings(formData.user_email);
    };

    const handleCancelAppointment = async (id) => {
        await axios.delete(`http://localhost:5000/appointments/${id}`);
        fetchBookings(formData.user_email);
    };

    return (
        <div className="min-h-screen bg-background p-8">
            <h1 className="text-4xl font-bold text-primary mb-8">Appointment Booking System</h1>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                {/* Booking Form */}
                <form onSubmit={handleBookAppointment} className="bg-white p-6 rounded-lg shadow-lg">
                    <h2 className="text-2xl font-semibold text-secondary mb-4">Book an Appointment</h2>
                    <input
                        type="text"
                        placeholder="Name"
                        className="w-full p-2 mb-4 border rounded"
                        value={formData.user_name}
                        onChange={(e) => setFormData({ ...formData, user_name: e.target.value })}
                    />
                    <input
                        type="email"
                        placeholder="Email"
                        className="w-full p-2 mb-4 border rounded"
                        value={formData.user_email}
                        onChange={(e) => setFormData({ ...formData, user_email: e.target.value })}
                    />
                    <input
                        type="date"
                        className="w-full p-2 mb-4 border rounded"
                        value={formData.date}
                        onChange={(e) => setFormData({ ...formData, date: e.target.value })}
                    />
                    <select
                        className="w-full p-2 mb-4 border rounded"
                        value={formData.time_slot}
                        onChange={(e) => setFormData({ ...formData, time_slot: e.target.value })}
                    >
                        <option value="">Select Time Slot</option>
                        {slots.map((slot, index) => (
                            <option key={index} value={slot.time_slot}>
                                {slot.time_slot}
                            </option>
                        ))}
                    </select>
                    <button type="submit" className="w-full bg-primary text-white p-2 rounded hover:bg-secondary">
                        Book Now
                    </button>
                </form>

                {/* Booked Appointments */}
                <div className="bg-white p-6 rounded-lg shadow-lg">
                    <h2 className="text-2xl font-semibold text-secondary mb-4">Your Appointments</h2>
                    {bookings.map((booking) => (
                        <div key={booking.id} className="mb-4 p-4 border rounded">
                            <p><strong>Date:</strong> {booking.date}</p>
                            <p><strong>Time:</strong> {booking.time_slot}</p>
                            <button
                                onClick={() => handleCancelAppointment(booking.id)}
                                className="mt-2 bg-red-500 text-white p-1 rounded hover:bg-red-600"
                            >
                                Cancel
                            </button>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default App;
