import React, { useState } from 'react';
import { format, startOfMonth, endOfMonth, eachDayOfInterval, addMonths, subMonths, isToday, isBefore } from 'date-fns';
import { ChevronLeft, ChevronRight, Clock } from 'lucide-react';

const AppointmentBooking = ({ onBookingComplete }) => {
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [currentMonth, setCurrentMonth] = useState(new Date());
  const [selectedSlot, setSelectedSlot] = useState(null);
  const [bookingStep, setBookingStep] = useState(1);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    notes: '',
  });
  const [availableSlots] = useState([
    { id: 1, time: '09:00 AM' },
    { id: 2, time: '10:00 AM' },
    { id: 3, time: '11:00 AM' },
    { id: 4, time: '02:00 PM' },
    { id: 5, time: '03:00 PM' },
    { id: 6, time: '04:00 PM' },
  ]);

  const getDaysInMonth = () => {
    const start = startOfMonth(currentMonth);
    const end = endOfMonth(currentMonth);
    return eachDayOfInterval({ start, end });
  };

  const nextMonth = () => setCurrentMonth(addMonths(currentMonth, 1));
  const prevMonth = () => setCurrentMonth(subMonths(currentMonth, 1));

  const handleDateSelect = (date) => {
    setSelectedDate(date);
    setBookingStep(2);
  };

  const handleSlotSelect = (slot) => {
    setSelectedSlot(slot);
    setBookingStep(3);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Add your booking logic here
    console.log('Booking submitted:', {
      date: selectedDate,
      slot: selectedSlot,
      ...formData
    });
    onBookingComplete?.();
  };

  return (
    <div className="max-w-4xl mx-auto p-6">
      {/* Progress Steps */}
      <div className="mb-8">
        <div className="flex justify-between items-center mb-4">
          {[1, 2, 3].map((step) => (
            <div
              key={step}
              className={`flex-1 h-2 ${
                step <= bookingStep ? 'bg-blue-500' : 'bg-gray-200'
              }`}
            />
          ))}
        </div>
        <div className="flex justify-between text-sm">
          <span className={bookingStep >= 1 ? 'text-blue-500' : 'text-gray-500'}>
            Select Date
          </span>
          <span className={bookingStep >= 2 ? 'text-blue-500' : 'text-gray-500'}>
            Choose Time
          </span>
          <span className={bookingStep >= 3 ? 'text-blue-500' : 'text-gray-500'}>
            Your Details
          </span>
        </div>
      </div>

      {bookingStep === 1 && (
        <div className="bg-white rounded-lg shadow-md p-6">
          <div className="flex justify-between items-center mb-6">
            <button
              onClick={prevMonth}
              className="p-2 hover:bg-gray-100 rounded-full"
            >
              <ChevronLeft className="w-5 h-5" />
            </button>
            <h2 className="text-xl font-semibold">
              {format(currentMonth, 'MMMM yyyy')}
            </h2>
            <button
              onClick={nextMonth}
              className="p-2 hover:bg-gray-100 rounded-full"
            >
              <ChevronRight className="w-5 h-5" />
            </button>
          </div>
          
          <div className="grid grid-cols-7 gap-2">
            {['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'].map((day) => (
              <div key={day} className="text-center font-medium text-gray-500 p-2">
                {day}
              </div>
            ))}
            {getDaysInMonth().map((date) => {
              const isDisabled = isBefore(date, new Date()) && !isToday(date);
              return (
                <button
                  key={date.toString()}
                  onClick={() => !isDisabled && handleDateSelect(date)}
                  disabled={isDisabled}
                  className={`p-4 rounded-lg text-center transition-colors ${
                    isDisabled
                      ? 'text-gray-300 cursor-not-allowed'
                      : format(date, 'yyyy-MM-dd') === format(selectedDate, 'yyyy-MM-dd')
                      ? 'bg-blue-500 text-white'
                      : 'hover:bg-gray-100'
                  } ${isToday(date) ? 'border-2 border-blue-500' : ''}`}
                >
                  {format(date, 'd')}
                </button>
              );
            })}
          </div>
        </div>
      )}

      {bookingStep === 2 && (
        <div className="bg-white rounded-lg shadow-md p-6">
          <h3 className="text-xl font-semibold mb-4">
            Select Time for {format(selectedDate, 'MMMM d, yyyy')}
          </h3>
          <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
            {availableSlots.map((slot) => (
              <button
                key={slot.id}
                onClick={() => handleSlotSelect(slot)}
                className={`flex items-center justify-center gap-2 p-4 rounded-lg border ${
                  selectedSlot?.id === slot.id
                    ? 'bg-blue-500 text-white border-blue-500'
                    : 'hover:bg-gray-50 border-gray-200'
                }`}
              >
                <Clock className="w-4 h-4" />
                {slot.time}
              </button>
            ))}
          </div>
        </div>
      )}

      {bookingStep === 3 && (
        <div className="bg-white rounded-lg shadow-md p-6">
          <h3 className="text-xl font-semibold mb-4">Enter Your Details</h3>
          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <label className="block text-gray-700 mb-2">Name</label>
              <input
                type="text"
                required
                className="w-full p-2 border rounded-lg"
                value={formData.name}
                onChange={(e) => setFormData({ ...formData, name: e.target.value })}
              />
            </div>
            <div>
              <label className="block text-gray-700 mb-2">Email</label>
              <input
                type="email"
                required
                className="w-full p-2 border rounded-lg"
                value={formData.email}
                onChange={(e) => setFormData({ ...formData, email: e.target.value })}
              />
            </div>
            <div>
              <label className="block text-gray-700 mb-2">Phone</label>
              <input
                type="tel"
                required
                className="w-full p-2 border rounded-lg"
                value={formData.phone}
                onChange={(e) => setFormData({ ...formData, phone: e.target.value })}
              />
            </div>
            <div>
              <label className="block text-gray-700 mb-2">Notes (Optional)</label>
              <textarea
                className="w-full p-2 border rounded-lg"
                rows="3"
                value={formData.notes}
                onChange={(e) => setFormData({ ...formData, notes: e.target.value })}
              />
            </div>
            <button
              type="submit"
              className="w-full bg-blue-500 text-white p-3 rounded-lg hover:bg-blue-600"
            >
              Confirm Booking
            </button>
          </form>
        </div>
      )}

      {/* Navigation Buttons */}
      <div className="mt-6 flex justify-between">
        {bookingStep > 1 && (
          <button
            onClick={() => setBookingStep(bookingStep - 1)}
            className="px-4 py-2 text-blue-500 hover:bg-blue-50 rounded-lg"
          >
            Back
          </button>
        )}
      </div>
    </div>
  );
};

export default AppointmentBooking;
