import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';

function Dashboard() {
  const [slots, setSlots] = useState([]);
  const [appointments, setAppointments] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [selectedSlot, setSelectedSlot] = useState(null);
  const [isBooking, setIsBooking] = useState(false);
  const [error, setError] = useState(null);
  const { token, logout } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    if (!token) {
      navigate('/login');
      return;
    }

    fetchSlots();
    fetchAppointments();
  }, [token, navigate]);

  const handleUnauthorized = () => {
    setError('Your session has expired. Please login again.');
    logout();
    navigate('/login');
  };

  const fetchSlots = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/slots', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setSlots(response.data);
      setError(null);
    } catch (error) {
      console.error('Error fetching slots:', error);
      if (error.response?.status === 401) {
        handleUnauthorized();
      } else {
        setError('Failed to fetch available slots. Please try again later.');
      }
    }
  };

  const fetchAppointments = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/appointments', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setAppointments(response.data);
      setError(null);
    } catch (error) {
      console.error('Error fetching appointments:', error);
      if (error.response?.status === 401) {
        handleUnauthorized();
      } else {
        setError('Failed to fetch appointments. Please try again later.');
      }
    }
  };

  const handleBooking = async (formData) => {
    setIsBooking(true);
    try {
      await axios.post(
        'http://localhost:5000/api/appointments',
        {
          slot_id: formData.slotId,
          name: formData.name,
          email: formData.email,
          phone: formData.phone,
          notes: formData.notes
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setShowForm(false);
      fetchSlots();
      fetchAppointments();
      setError(null);
    } catch (error) {
      console.error('Error booking appointment:', error);
      if (error.response?.status === 401) {
        handleUnauthorized();
      } else {
        setError('Failed to book appointment. Please try again later.');
      }
    } finally {
      setIsBooking(false);
    }
  };

  const cancelAppointment = async (appointmentId) => {
    try {
      await axios.delete(
        `http://localhost:5000/api/appointments/${appointmentId}`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      fetchAppointments();
      fetchSlots();
      setError(null);
    } catch (error) {
      console.error('Error canceling appointment:', error);
      if (error.response?.status === 401) {
        handleUnauthorized();
      } else {
        setError('Failed to cancel appointment. Please try again later.');
      }
    }
  };

  if (!token) {
    return null; // Don't render anything while redirecting
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="flex justify-between items-center mb-8">
        <h1 className="text-2xl font-bold">Appointment Dashboard</h1>
        <button
          onClick={logout}
          className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
        >
          Logout
        </button>
      </div>

      {error && (
        <div className="mb-6 p-4 bg-red-50 border border-red-200 text-red-600 rounded-lg">
          {error}
        </div>
      )}

      <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
      <div>
          <h2 className="text-xl font-semibold mb-4">Available Slots</h2>
          <div className="space-y-4">
            {slots.map((slot) => (
              <div
                key={slot.id}
                className="border p-4 rounded shadow-sm flex justify-between items-center"
              >
                <div>
                  <p className="font-medium">{new Date(slot.date).toLocaleDateString()}</p>
                  <p>{`${slot.start_time} - ${slot.end_time}`}</p>
                </div>
                <button
                  onClick={() => {
                    setSelectedSlot(slot);
                    setShowForm(true);
                  }}
                  className="bg-indigo-500 text-white px-4 py-2 rounded hover:bg-indigo-600"
                >
                  Book
                </button>
              </div>
            ))}
          </div>
        </div>

        <div>
          <h2 className="text-xl font-semibold mb-4">Your Appointments</h2>
          <div className="space-y-4">
            {appointments.map((appointment) => (
              <div
                key={appointment.id}
                className="border p-4 rounded shadow-sm flex justify-between items-center"
              >
                <div>
                  <p className="font-medium">
                    {new Date(appointment.date).toLocaleDateString()}
                  </p>
                  <p>{`${appointment.start_time} - ${appointment.end_time}`}</p>
                  <p className="text-gray-600">
                    Status: {appointment.status}
                  </p>
                </div>
                {appointment.status !== 'cancelled' && (
                  <button
                    onClick={() => cancelAppointment(appointment.id)}
                    className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
                  >
                    Cancel
                  </button>
                )}
              </div>
            ))}
          </div>
        </div>
      

      </div>

      {showForm && selectedSlot && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-lg max-h-[90vh] overflow-y-auto">
            <AppointmentForm
              slot={selectedSlot}
              onSubmit={handleBooking}
              isLoading={isBooking}
              onClose={() => setShowForm(false)}
            />
          </div>
        </div>
      )}
    </div>
  );
}

export default Dashboard;
