import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { format } from 'date-fns';
import { LoginForm, SignupForm } from './components/Auth';
import { Calendar, BookingForm } from './components/Appointment';
import { AppointmentList, AdminPanel } from './components/AppointmentManagement';
import { Link, useNavigate } from 'react-router-dom';

// Layout wrapper for protected routes
const ProtectedRoute = ({ children }) => {
  const token = localStorage.getItem('token');
  return token ? children : <Navigate to="/login" replace />;
};

// Admin route wrapper
const AdminRoute = ({ children }) => {
  const [isAdmin, setIsAdmin] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const checkAdmin = async () => {
      try {
        const response = await fetch('/api/auth/check-admin', {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          }
        });
        setIsAdmin(response.ok);
      } catch (error) {
        setIsAdmin(false);
      }
      setLoading(false);
    };

    checkAdmin();
  }, []);

  if (loading) return <div>Loading...</div>;
  return isAdmin ? children : <Navigate to="/dashboard" replace />;
};

// Navbar
const Navbar = () => {
  const token = localStorage.getItem('token');
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  return (
    <nav className="bg-blue-600 p-4">
      <div className="max-w-7xl mx-auto flex justify-between items-center">
        <div className="text-white font-bold text-xl">Appointment System</div>
        {token ? (
          <div className="flex space-x-4">
            <Link to="/appointmentbooking" className="text-white hover:text-blue-200">Book Appointment</Link>
            <Link to="/dashboard" className="text-white hover:text-blue-200">Dashboard</Link>
            <Link to="/appointments" className="text-white hover:text-blue-200">My Appointments</Link>
            <button onClick={handleLogout} className="text-white hover:text-blue-200">Logout</button>
          </div>
        ) : (
          <div className="flex space-x-4">
            <Link to="/login" className="text-white hover:text-blue-200">Login</Link>
            <Link to="/signup" className="text-white hover:text-blue-200">Sign Up</Link>
          </div>
        )}
      </div>
    </nav>
  );
};

// Dashboard
const Dashboard = () => {
  const [selectedDate, setSelectedDate] = useState(null);
  const [availableSlots, setAvailableSlots] = useState([]);
  const [showBookingForm, setShowBookingForm] = useState(false);
  const [loading, setLoading] = useState(false);

  // Handle date selection
  const handleDateSelect = async (date) => {
    console.log("✅ Date selected:", format(date, 'yyyy-MM-dd'));
    setSelectedDate(date);
    setShowBookingForm(false); // Reset booking form while fetching data

    try {
      setLoading(true);
      const response = await fetch(`/api/slots?date=${format(date, 'yyyy-MM-dd')}`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });

      if (!response.ok) throw new Error('Failed to fetch slots');

      const data = await response.json();
      console.log("✅ Available Slots:", data);

      setAvailableSlots(data);
      setShowBookingForm(data.length > 0);
    } catch (error) {
      console.error('❌ Error fetching slots:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-8">Book an Appointment</h1>

      {/* Calendar Component */}
      <Calendar onDateSelect={handleDateSelect} />

      {/* Show loading indicator while fetching slots */}
      {loading && <p className="text-center mt-4">Loading available slots...</p>}

      {/* Booking Form appears only if slots are available */}
      {showBookingForm ? (
        <BookingForm 
          selectedDate={selectedDate} 
          availableSlots={availableSlots} 
          onBookingComplete={() => setShowBookingForm(false)}
        />
      ) : selectedDate && !loading ? (
        <p className="text-center mt-4 text-red-500">No available slots for this date.</p>
      ) : null}
    </div>
  );
};


// Main App Component
const App = () => {
  return (
    <Router>
      <Navbar />
      <div className="container mx-auto px-4 py-8">
        <Routes>
          <Route path="/login" element={<LoginForm />} />
          <Route path="/signup" element={<SignupForm />} />
          <Route path="/dashboard" element={<ProtectedRoute><Dashboard /></ProtectedRoute>} />
          <Route path="/appointmentbooking" element={<ProtectedRoute><Dashboard /></ProtectedRoute>} />
          <Route path="/appointments" element={<ProtectedRoute><AppointmentList /></ProtectedRoute>} />
          <Route path="/admin" element={<ProtectedRoute><AdminRoute><AdminPanel /></AdminRoute></ProtectedRoute>} />
          <Route path="/" element={localStorage.getItem('token') ? <Navigate to="/dashboard" replace /> : <Navigate to="/login" replace />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
