// src/App.js
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, Link, useNavigate } from 'react-router-dom';
import { format } from 'date-fns';

// Calendar Component
const Calendar = ({ onDateSelect }) => {
  const [selectedDate, setSelectedDate] = useState(new Date());

  const getDaysInMonth = () => {
    const start = startOfMonth(selectedDate);
    const end = endOfMonth(selectedDate);
    return eachDayOfInterval({ start, end });
  };

  return (
    <div className="max-w-4xl mx-auto mt-8">
      <div className="grid grid-cols-7 gap-2">
        {['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'].map(day => (
          <div key={day} className="text-center font-bold p-2">
            {day}
          </div>
        ))}
        {getDaysInMonth().map(date => (
          <div
            key={date.toString()}
            onClick={() => onDateSelect(date)}
            className={`p-4 border rounded cursor-pointer text-center
              ${format(date, 'yyyy-MM-dd') === format(selectedDate, 'yyyy-MM-dd')
                ? 'bg-blue-500 text-white'
                : 'hover:bg-gray-100'}`}
          >
            {format(date, 'd')}
          </div>
        ))}
      </div>
    </div>
  );
};

// Booking Form Component
const BookingForm = ({ selectedDate, availableSlots, onBookingComplete }) => {
  const [bookingData, setBookingData] = useState({
    timeSlot: '',
    notes: ''
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('/api/appointments', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify({
          ...bookingData,
          date: format(selectedDate, 'yyyy-MM-dd')
        }),
      });

      if (response.ok) {
        onBookingComplete();
        alert('Appointment booked successfully!');
      }
    } catch (error) {
      console.error('Booking failed:', error);
      alert('Booking failed. Please try again.');
    }
  };

  return (
    <div className="max-w-md mx-auto mt-8 p-6 bg-white rounded-lg shadow-md">
      <h3 className="text-xl font-bold mb-4">
        Book Appointment for {format(selectedDate, 'MMMM d, yyyy')}
      </h3>
      <form onSubmit={handleSubmit}>
        <div className="mb-4">
          <label className="block text-gray-700 mb-2">Time Slot</label>
          <select
            className="w-full p-2 border rounded"
            value={bookingData.timeSlot}
            onChange={(e) => setBookingData({...bookingData, timeSlot: e.target.value})}
            required
          >
            <option value="">Select a time</option>
            {availableSlots.map(slot => (
              <option key={slot.id} value={slot.time}>
                {slot.time}
              </option>
            ))}
          </select>
        </div>
        <div className="mb-4">
          <label className="block text-gray-700 mb-2">Notes</label>
          <textarea
            className="w-full p-2 border rounded"
            value={bookingData.notes}
            onChange={(e) => setBookingData({...bookingData, notes: e.target.value})}
            rows="3"
          />
        </div>
        <button 
          type="submit"
          className="w-full bg-blue-500 text-white p-2 rounded hover:bg-blue-600"
        >
          Book Appointment
        </button>
      </form>
    </div>
  );
};

// Booking Wrapper Component
const BookingWrapper = () => {
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [availableSlots, setAvailableSlots] = useState([]);

  useEffect(() => {
    const fetchSlots = async () => {
      try {
        const response = await fetch(`/api/slots?date=${format(selectedDate, 'yyyy-MM-dd')}`, {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          }
        });
        const data = await response.json();
        setAvailableSlots(data);
      } catch (error) {
        console.error('Error fetching slots:', error);
      }
    };

    fetchSlots();
  }, [selectedDate]);

  const handleBookingComplete = () => {
    // Additional logic if needed
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-8">Book an Appointment</h1>
      <Calendar onDateSelect={setSelectedDate} />
      <BookingForm
        selectedDate={selectedDate}
        availableSlots={availableSlots}
        onBookingComplete={handleBookingComplete}
