// Protected Route Component
const ProtectedRoute = ({ children }) => {
  const token = localStorage.getItem('token');
  return token ? children : <Navigate to="/login" replace />;
};

// Navbar Component
const Navbar = () => {
  const token = localStorage.getItem('token');
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  return (
    <nav className="bg-blue-600 p-4">
      <div className="max-w-7xl mx-auto flex justify-between items-center">
        <div className="text-white font-bold text-xl">Appointment System</div>
        {token ? (
          <div className="flex space-x-4">
            <Link to="/appointmentbooking" className="text-white hover:text-blue-200">
              Book Appointment
            </Link>
            <button onClick={handleLogout} className="text-white hover:text-blue-200">
              Logout
            </button>
          </div>
        ) : (
          <div className="flex space-x-4">
            <Link to="/login" className="text-white hover:text-blue-200">
              Login
            </Link>
            <Link to="/signup" className="text-white hover:text-blue-200">
              Sign Up
            </Link>
          </div>
        )}
      </div>
    </nav>
  );
};

// Main App Component
const App = () => {
  return (
    <Router>
      <div className="min-h-screen bg-gray-50">
        <Navbar />
        <div className="container mx-auto px-4 py-8">
          <Routes>
            <Route path="/login" element={<LoginForm />} />
            <Route path="/signup" element={<SignupForm />} />

            <Route 
              path="/appointmentbooking" 
              element={
                <ProtectedRoute>
                  <BookingWrapper />
                </ProtectedRoute>
              } 
            />

            <Route 
              path="/" 
              element={
                localStorage.getItem('token') ? 
                  <Navigate to="/appointmentbooking" replace /> : 
                  <Navigate to="/login" replace />
              } 
            />
          </Routes>
        </div>
      </div>
    </Router>
  );
};

export default App;
