// src/App.js
import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { useState, useEffect } from 'react';
import { format } from 'date-fns'; // Add this import
import { LoginForm, SignupForm } from './components/Auth';
import { Calendar, BookingForm } from './components/Appointment';
import { AppointmentList, AdminPanel } from './components/AppointmentManagement';
import { Link, useNavigate } from 'react-router-dom';

// Layout wrapper for protected routes
const ProtectedRoute = ({ children }) => {
  const token = localStorage.getItem('token');
  if (!token) {
    return <Navigate to="/login" replace />;
  }
  return children;
};

// Admin route wrapper
const AdminRoute = ({ children }) => {
  const [isAdmin, setIsAdmin] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const checkAdmin = async () => {
      try {
        const response = await fetch('/api/auth/check-admin', {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          }
        });
        setIsAdmin(response.ok);
      } catch (error) {
        setIsAdmin(false);
      }
      setLoading(false);
    };

    checkAdmin();
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (!isAdmin) {
    return <Navigate to="/dashboard" replace />;
  }

  return children;
};

// Navbar component
const Navbar = () => {
  const token = localStorage.getItem('token');
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  return (
    <nav className="bg-blue-600 p-4">
      <div className="max-w-7xl mx-auto flex justify-between items-center">
        <div className="text-white font-bold text-xl">Appointment System</div>
        {token ? (
          <div className="flex space-x-4">
            <Link
              to="/appointmentbooking"
              className="text-white hover:text-blue-200"
            >
              Book Appointment
            </Link>
            <Link to="/dashboard" className="text-white hover:text-blue-200">
              Dashboard
            </Link>
            <Link to="/appointments" className="text-white hover:text-blue-200">
              My Appointments
            </Link>
            <button
              onClick={handleLogout}
              className="text-white hover:text-blue-200"
            >
              Logout
            </button>
          </div>
        ) : (
          <div className="flex space-x-4">
            <Link to="/login" className="text-white hover:text-blue-200">
              Login
            </Link>
            <Link to="/signup" className="text-white hover:text-blue-200">
              Sign Up
            </Link>
          </div>
        )}
      </div>
    </nav>
  );
};
<Route
  path="/appointmentbooking"
  element={
    <ProtectedRoute>
      <BookingWrapper />
    </ProtectedRoute>
  }
/>

// Add this new component
const BookingWrapper = () => {
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [availableSlots, setAvailableSlots] = useState([]);

  useEffect(() => {
    const fetchSlots = async () => {
      try {
        const response = await fetch(`/api/slots?date=${format(selectedDate, 'yyyy-MM-dd')}`, {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          }
        });
        const data = await response.json();
        setAvailableSlots(data);
      } catch (error) {
        console.error('Error fetching slots:', error);
      }
    };

    fetchSlots();
  }, [selectedDate]);

  const handleBookingComplete = () => {
    // Handle booking completion logic
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <Calendar onDateSelect={setSelectedDate} />
      <BookingForm
        selectedDate={selectedDate}
        availableSlots={availableSlots}
        onBookingComplete={handleBookingComplete}
      />
    </div>
  );
};

// Dashboard component
const Dashboard = () => {
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [availableSlots, setAvailableSlots] = useState([]);
  const [showBookingForm, setShowBookingForm] = useState(false);

  const handleDateSelect = async (date) => {
    setSelectedDate(date);
    try {
      const response = await fetch(`/api/slots?date=${format(date, 'yyyy-MM-dd')}`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });
      const data = await response.json();
      setAvailableSlots(data);
      setShowBookingForm(true);
    } catch (error) {
      console.error('Error fetching slots:', error);
    }
  };

  const handleBookingComplete = () => {
    setShowBookingForm(false);
    // Optionally show a success message or redirect
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-8">Book an Appointment</h1>
      <Calendar onDateSelect={handleDateSelect} />
      {showBookingForm && (
        <BookingForm
          selectedDate={selectedDate}
          availableSlots={availableSlots}
          onBookingComplete={handleBookingComplete}
        />
      )}
    </div>
  );
};

// Main App component
const App = () => {
  return (
    <Router>
      <div className="min-h-screen bg-gray-50">
        <Navbar />
        <div className="container mx-auto px-4 py-8">
          <Routes>
            {/* Public routes */}
            <Route path="/login" element={<LoginForm />} />
            <Route path="/signup" element={<SignupForm />} />

            {/* Protected routes */}
            <Route
              path="/dashboard"
              element={
                <ProtectedRoute>
                  <Dashboard />
                </ProtectedRoute>
              }
            />

<Route
  path="/appointmentbooking"
  element={
    <ProtectedRoute>
      <Dashboard />  // Use Dashboard instead of just BookingForm
    </ProtectedRoute>
  }
/>

            <Route
              path="/appointments"
              element={
                <ProtectedRoute>
                  <AppointmentList />
                </ProtectedRoute>
              }
            />

            {/* Admin routes */}
            <Route
              path="/admin"
              element={
                <ProtectedRoute>
                  <AdminRoute>
                    <AdminPanel />
                  </AdminRoute>
                </ProtectedRoute>
              }
            />

            {/* Redirect root to dashboard or login */}
            <Route
              path="/"
              element={
                localStorage.getItem('token') ? (
                  <Navigate to="/dashboard" replace />
                ) : (
                  <Navigate to="/login" replace />
                )
              }
            />
          </Routes>
        </div>
      </div>
    </Router>
  );
};

export default App;
