// AppointmentList.js
import React, { useState, useEffect } from 'react';
import { format } from 'date-fns';


const AppointmentList = () => {
  const [appointments, setAppointments] = useState([]);

  useEffect(() => {
    fetchAppointments();
  }, []);

  const fetchAppointments = async () => {
    try {
      const response = await fetch('http://localhost:3000/api/appointments', {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });
      const data = await response.json();
      setAppointments(data);
    } catch (error) {
      console.error('Error fetching appointments:', error);
    }
  };

  const handleCancel = async (id) => {
    try {
      const response = await fetch(`http://localhost:3000/api/appointments/${id}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });

      if (response.ok) {
        fetchAppointments();
      }
    } catch (error) {
      console.error('Error canceling appointment:', error);
    }
  };

  return (
    <div className="max-w-4xl mx-auto mt-8">
      <h2 className="text-2xl font-bold mb-6">Your Appointments</h2>
      <div className="grid gap-4">
        {appointments.map(appointment => (
          <div key={appointment.id} className="p-4 border rounded shadow-sm">
            <div className="flex justify-between items-center">
              <div>
                <p className="font-bold">
                  {format(new Date(appointment.date), 'MMMM d, yyyy')}
                </p>
                <p className="text-gray-600">{appointment.timeSlot}</p>
                {appointment.notes && (
                  <p className="text-gray-500 mt-2">{appointment.notes}</p>
                )}
              </div>
              <button
                onClick={() => handleCancel(appointment.id)}
                className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
              >
                Cancel
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};
const AdminPanel = () => {
  const [appointments, setAppointments] = useState([]);
  const [timeSlots, setTimeSlots] = useState([]);
  const [newSlot, setNewSlot] = useState({ time: '', capacity: 1 });

  useEffect(() => {
    fetchAdminData();
  }, []);

  const fetchAdminData = async () => {
    try {
      const [appointmentsRes, slotsRes] = await Promise.all([
        fetch('/api/admin/appointments', {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          }
        }),
        fetch('/api/admin/slots', {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          }
        })
      ]);

      const appointmentsData = await appointmentsRes.json();
      const slotsData = await slotsRes.json();

      setAppointments(appointmentsData);
      setTimeSlots(slotsData);
    } catch (error) {
      console.error('Error fetching admin data:', error);
    }
  };

  const handleAddSlot = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:3000//api/admin/slots', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify(newSlot),
      });

      if (response.ok) {
        setNewSlot({ time: '', capacity: 1 });
        fetchAdminData();
      }
    } catch (error) {
      console.error('Error adding slot:', error);
    }
  };

  const handleDeleteSlot = async (slotId) => {
    try {
      const response = await fetch(`/api/admin/slots/${slotId}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });

      if (response.ok) {
        fetchAdminData();
      }
    } catch (error) {
      console.error('Error deleting slot:', error);
    }
  };

  const handleUpdateStatus = async (appointmentId, status) => {
    try {
      const response = await fetch(`http://localhost:3000/api/admin/appointments/${appointmentId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify({ status }),
      });

      if (response.ok) {
        fetchAdminData();
      }
    } catch (error) {
      console.error('Error updating appointment status:', error);
    }
  };

  return (
    <div className="max-w-4xl mx-auto mt-8">
      <h2 className="text-2xl font-bold mb-6">Admin Panel</h2>
      
      {/* Time Slots Management */}
      <div className="mb-8">
        <h3 className="text-xl font-bold mb-4">Manage Time Slots</h3>
        <form onSubmit={handleAddSlot} className="mb-4">
          <div className="flex gap-4">
            <input
              type="time"
              className="p-2 border rounded"
              value={newSlot.time}
              onChange={(e) => setNewSlot({...newSlot, time: e.target.value})}
              required
            />
            <input
              type="number"
              className="p-2 border rounded w-24"
              value={newSlot.capacity}
              onChange={(e) => setNewSlot({...newSlot, capacity: parseInt(e.target.value)})}
              min="1"
              required
            />
            <button 
              type="submit"
              className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
            >
              Add Slot
            </button>
          </div>
        </form>

        <div className="grid gap-4">
          {timeSlots.map(slot => (
            <div key={slot.id} className="p-4 border rounded flex justify-between items-center">
              <div>
                <p className="font-bold">{slot.time}</p>
                <p className="text-gray-600">Capacity: {slot.capacity}</p>
              </div>
              <button
                onClick={() => handleDeleteSlot(slot.id)}
                className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
              >
                Delete
              </button>
            </div>
          ))}
        </div>
      </div>

      {/* All Appointments */}
      <div>
        <h3 className="text-xl font-bold mb-4">All Appointments</h3>
        <div className="grid gap-4">
          {appointments.map(appointment => (
            <div key={appointment.id} className="p-4 border rounded">
              <div className="flex justify-between items-center">
                <div>
                  <p className="font-bold">
                    {format(new Date(appointment.date), 'MMMM d, yyyy')}
                  </p>
                  <p className="text-gray-600">{appointment.timeSlot}</p>
                  <p className="text-gray-500">User: {appointment.userName}</p>
                  {appointment.notes && (
                    <p className="text-gray-500 mt-2">{appointment.notes}</p>
                  )}
                </div>
                <div className="flex gap-2">
                  <button
                    onClick={() => handleUpdateStatus(appointment.id, 'confirmed')}
                    className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600"
                  >
                    Confirm
                  </button>
                  <button
                    onClick={() => handleUpdateStatus(appointment.id, 'cancelled')}
                    className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
                  >
                    Cancel
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export { AppointmentList, AdminPanel };
